import React from 'react';
import { observer } from 'mobx-react';
import { Link } from 'react-router-dom';
import SessionStore from '../../stores/session.store';
import './login.scss'

export default class LoginPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            submitted: false
        };
        this.SessionStore = SessionStore;
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    componentDidMount() {
        const { loggingIn } = this.props;
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();
        this.setState({ submitted: true });
        const { email, password } = this.state;
        console.log(email, password)
        this.SessionStore.login(email, password);
    }

    render() {
        const { email, password, submitted } = this.state;
        return (
            <div className="loginpage">
                <div className="title"><i className="fas fa-sign-in-alt" /> {"  "}LOGIN</div>
                <br />
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !email ? ' has-error' : '')}>
                        <label className="properties" htmlFor="email">Email</label>
                        <input type="text" className="form-control" name="email" value={email} onChange={this.handleChange} />
                        {submitted && !email &&
                            <div className="help-block">Email is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label className="properties" htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <br />
                    <div className="form-group">
                        <button className="button">Login</button>
                        {/* {loggingIn &&
                            <img src="data:image/gif;base64," />
                        } */}
                        <Link to="/register" className="btn btn-link button-link">Register</Link>
                    </div>
                </form>
            </div>
        );
    }
}